import React, { useState } from 'react';
import { connect } from 'react-redux';
import { actions } from '../Redux/Store/actions';
import { compose } from "redux";
import { withRouter } from 'react-router-dom';

const mapDispatchToProps = (dispatch) => ({
    setDataTable: (data) => dispatch(actions.setDataTable(data)),
})


function mapStateToProps(state) {
    return {
        dataTable: state.userReducer.dataTable
    }
}
export default compose(withRouter, connect(mapStateToProps, mapDispatchToProps))(function TableDynamic(props) {
    const { dataTable, setDataTable } = props;
    const[arrField,setarrField]=useState([])
    function getAllData() {
        getKeys()
        fetch("http://localhost:3001/getAllData", {
            method: "get",
            headers: { "Content-Type": "application/json" },
        })
            .then(res => res.json())
            .then((data) => {
                console.log(data)
                setDataTable(data);
                setarrField(Object.keys(data[0]))
             
            })
            .catch(err => console.log(err))
           
    }
    return (

        <>
            <br />
            <button type="button" class="btn btn-outline-primary" onClick={getAllData}>Click me</button>
            <br /><br />
            <table className="table">
                <thead className="thead-primary">
                    <tr>
                        {arrField.map((k) => (
                            <th scope="col">{k}</th>))}
                    </tr>
                </thead>
                <tbody>
                   { dataTable.map((x, k) => (
                           <tr>
                          {arrField.map((key) => (
                            <td scope="col">{x[key]}</td>))}
                           </tr>))}
                </tbody>
            </table>
        </>
    )
})
