import React, { useState } from 'react';
import { connect } from 'react-redux';
import { actions } from '../Redux/Store/actions';
import { compose } from "redux";
import { withRouter } from 'react-router-dom';

const mapDispatchToProps = (dispatch) => ({
    setDataTable: (data) => dispatch(actions.setDataTable(data)),
})


function mapStateToProps(state) {
    return {
        dataTable: state.userReducer.dataTable
    }
}
export default compose(withRouter, connect(mapStateToProps, mapDispatchToProps))(function TableDynamic(props) {
    const { dataTable, setDataTable } = props;
    const[arrField,setarrField]=useState([])
    const [skip,setSkip]=useState(0)
    function getKeys(){
    
        fetch("http://localhost:3001/getAllKeys", {
            method: "get",
            headers: { "Content-Type": "application/json" },
            
        })
            .then(res => res.json())
            .then((data) => {
                console.log(data)
                setarrField(data)
             
            })
            .catch(err => console.log(err))
           

    }
    const addUp=()=>{
      setSkip()
    }
   
    function getAllData(limit,skip) {
        getKeys();
        alert(skip)
        fetch("http://localhost:3001/getAllData", {
            method: "post",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({limit:limit,skip:skip}) 
        })
            .then(res => res.json())
            .then((data) => {
                console.log(data)
                setDataTable(data);
             
            })
            .catch(err => console.log(err))
           setSkip(skip+40)
    }
    return (

        <>
            <br />
            <button type="button" class="btn btn-outline-primary" onClick={()=>getAllData(40,0)}>Start</button>
            <br /><br />
            <button type="button" class="btn btn-outline-primary" onClick={()=>{getAllData(40,skip);addUp()}}>Add Up</button>

            <table className="table">
                <thead className="thead-primary">
                    <tr>
                        {arrField.map((k) => (
                            <th scope="col">{k}</th>))}
                    </tr>
                </thead>
                <tbody>
                   { dataTable.map((x, k) => (
                           <tr>
                          {arrField.map((key) => (
                            <td scope="col">{x[key]}</td>))}
                           </tr>))}
                </tbody>
            </table>
            <button type="button" class="btn btn-outline-primary" onClick={()=>getAllData(40,skip)}>Add Down</button> 
        </>
    )
})
