import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { actions } from '../Redux/Store/actions';
import { compose } from "redux";

const mapDispatchToProps = (dispatch) => ({
    setDataTable: (data) => dispatch(actions.setDataTable(data)),
})


function mapStateToProps(state) {
    return {
        dataTable: state.tableReducer.dataTable
    }
}
export default compose(connect(mapStateToProps, mapDispatchToProps))(function TableDynamic(props) {
    const { dataTable, setDataTable } = props;
    const [arrField, setarrField] = useState([])
    const [skip, setSkip] = useState(0)

    const handleScroll = () => {
        const bottom = Math.ceil(window.innerHeight + window.scrollY) >= document.documentElement.scrollHeight;
        const top = Math.ceil(window.scrollY) == 0;
        if (bottom) {
            getAllData("down")
        }
        if (top) {
            getAllData("up")
        }
    };
    useEffect(() => {
        window.addEventListener('scroll', handleScroll, {
            passive: true
        });

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    });


    function getKeys() {

        fetch("http://localhost:3001/getAllKeys", {
            method: "get",
            headers: { "Content-Type": "application/json" },

        })
            .then(res => res.json())
            .then((data) => {
                console.log(data)
                setarrField(data)

            })
            .catch(err => console.log(err))


    }
    useEffect(() => {
        fetch("http://localhost:3001/getAllData", {
            method: "post",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ limit: 40, skip: skip })
        })
            .then(res => res.json())
            .then((data) => {
                console.log(data)
                setDataTable(data);

            })
            .catch(err => console.log(err))
    }, [skip])


    function getAllData(status) {
        getKeys();
        switch (status) {
            case "up":
                setSkip(skip - 20)
                break;
            case "down":
                setSkip(skip + 20)
                break;
            default:
                setSkip(0);
        }

    }
    return (

        <>
            <br />
            <br />
            {!dataTable.lengt > 0 ? <button type="button" class="btn btn-outline-primary" onClick={() => { getAllData(); }}>Start</button> : ""}
            <br /><br />
            <div>
                <table className="table table-striped table-hover">
                    <thead className="thead-primary">
                        <tr>
                            {arrField.map((k) => (
                                <th scope="col">{k}</th>
                            ))}
                            {arrField.length > 0 ? <th>ttt</th> : ""}
                        </tr>
                    </thead>
                    <tbody>
                        {dataTable.map((x, k) => (
                            <tr>
                                {arrField.map((key) => (
                                    <td scope="col">{x[key]}</td>))}
                                {arrField.length > 0 ? <div class="btn-group">
                                    <button type="button" class="btn btn-danger dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
                                        Action
                                    </button>
                                    <ul class="dropdown-menu">
                                        <li><a class="dropdown-item" href="#">Action</a></li>
                                        <li><a class="dropdown-item" href="#">Another action</a></li>
                                        <li><a class="dropdown-item" href="#">Something else here</a></li>
                                       
                                            <li><a class="dropdown-item" href="#">Separated link</a></li>
                                       </ul>
                               </div>:""}
                            </tr>))}

                    </tbody>
                </table>
                </div>
        </>
            )
})
