import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { actions } from '../Redux/Store/actions';
import { compose } from "redux";

const mapDispatchToProps = (dispatch) => ({
    setDataTable: (data) => dispatch(actions.setDataTable(data)),
})


function mapStateToProps(state) {
    return {
        dataTable: state.tableReducer.dataTable
    }
}
export default compose(connect(mapStateToProps, mapDispatchToProps))(function TableDynamic(props) {
    const { dataTable, setDataTable } = props;
    const [arrField, setarrField] = useState([])
    const [skip, setSkip] = useState(0)

    function isBottom(el) {
        return el.getBoundingClientRect().bottom <= window.innerHeight;
      }
     
      
      useEffect(()=>{
        document.addEventListener('scroll', trackScrolling);
      })
      
    //   componentWillUnmount() {
    //     document.removeEventListener('scroll', this.trackScrolling);
    //   }
      
    //  const trackScrolling = () => {
    //     const wrappedElement = document.getElementById('header');
    //     if (wrappedElement.scrollHeight - wrappedElement.scrollTop === wrappedElement.clientHeight) {
    //       console.log('header bottom reached');
    //       document.removeEventListener('scroll', trackScrolling);
    //     }
    //   };




    function getKeys() {

        fetch("http://localhost:3001/getAllKeys", {
            method: "get",
            headers: { "Content-Type": "application/json" },

        })
            .then(res => res.json())
            .then((data) => {
                console.log(data)
                setarrField(data)

            })
            .catch(err => console.log(err))


    }
    useEffect(()=>{
        fetch("http://localhost:3001/getAllData", {
            method: "post",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ limit: 40, skip: skip })
        })
            .then(res => res.json())
            .then((data) => {
                console.log(data)
                setDataTable(data);

            })
            .catch(err => console.log(err))
    },[skip])


    function getAllData(status) {
        getKeys();
        alert(skip)
        switch (status) {
            case "up":
                setSkip(skip - 20)
                break;
            case "down":
                setSkip(skip + 20)
                break;
            default:
                setSkip(0);
        }
        
    }
    return (

        <>
        <body onScroll={handleScroll}>
            <header></header>
            <br />
            <button type="button" class="btn btn-outline-primary" onClick={() => getAllData( )}>Start</button>
            <br /><br />
            <button type="button" class="btn btn-outline-primary" onClick={() => { getAllData("up"); }}>Add Up</button>

            <table className="table">
                <thead className="thead-primary">
                    <tr>
                        {arrField.map((k) => (
                            <th scope="col">{k}</th>))}
                    </tr>
                </thead>
                <tbody>
                    {dataTable.map((x, k) => (
                        <tr>
                            {arrField.map((key) => (
                                <td scope="col">{x[key]}</td>))}
                        </tr>))}
                </tbody>
            </table>
            <button type="button" class="btn btn-outline-primary" onClick={() => { getAllData("down"); }}>Add Down</button>
            </body>
        </>
    )
})
